import React, { useState, useEffect } from 'react';
import { Container, Row } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import { invoke } from '@tauri-apps/api/tauri';
import { useTranslation } from 'react-i18next';
import './Help.css';

export default function Help({ onClose, visible, onSave }) {
    const { t, i18n } = useTranslation("global");
    const [selectedLanguage, setSelectedLanguage] = useState(''); // початкове значення мови
    const [selectedTheme, setSelectedTheme] = useState('standard-dark'); // початкова тема
    const [autosaveInterval, setAutosaveInterval] = useState(0); // початковий інтервал автозбереження

    useEffect(() => {
        const storedLang = localStorage.getItem('language');
        if (storedLang) {
            handleLanguageChange(storedLang);
        } else {
            handleLanguageChange('ua');
        }
    }, []);

    const handleLanguageChange = (lang) => {
        i18n.changeLanguage(lang);
        setSelectedLanguage(lang);
        localStorage.setItem('language', lang);
    };

    const handleLanguageSelectChange = (event) => {
        const lang = event.target.value;
        handleLanguageChange(lang);
    };

    const handleThemeChange = async (event) => {
        const themeName = event.target.value;
        setSelectedTheme(themeName);
        try {
            await invoke('theme_change', { themeName });
        } catch (error) {
            console.error('Error invoking theme change:', error);
        }
    };

    const handleAutosaveSelectChange = (event) => {
        const value = event.target.value;
        const interval = parseInt(value, 10);
        let adjustedInterval = interval * 60;
        if (interval === 10) {
            adjustedInterval = 10 * 60; // змінюємо 20 на 10 * 60 (10 хвилин)
        }
        setAutosaveInterval(adjustedInterval);
    };    

    useEffect(() => {
        if (autosaveInterval > 0) {
            const intervalId = setInterval(() => {
                onSave();
            }, autosaveInterval * 1000);

            return () => {
                clearInterval(intervalId);
            };
        }
    }, [autosaveInterval, onSave]);


    const [selectedTest, setSelectedTest] = useState(0); // початковий інтервал автозбереження
    const handleTest = (event) => {
        const value = event.target.value;
        setSelectedTest(value);
    };


    return (
        <Container className={`help-container ${visible ? "visible" : "hidden"}`}>
            <Row className='help-close-button'>
                <button onClick={onClose}><b>X</b></button>
            </Row>
            <Row className='help-content-container'>
                <div className='help-content'>
                    <h1>NUB IDE</h1>
                    <p>Це середовище розробки створене спеціально для мов програмування NukLang, Удав та Based</p>
                    <p>Його основною ідеєю є робота через термінал, але не повна</p>
                    <p>Інтерпретатори мов та компілятори були встановлені автоматично</p>
                    <p>Оскільки мови не сильно працюють із сторонніми файлами, то робота у даному редакторі коду також здійснюється у межах одного файлу</p>
                    <hr></hr>
                    <p>Ide має такі команди для терміналу:</p>
                    <p>- cd [path]</p>
                    <p>- ls </p>
                    <p>- pwd</p>
                    <p>- open - відкриває файл</p>
                    <p>- save - зберігає файл</p>
                    <p>- save as - зберегти файл як</p>
                    <p>- translate - викликає меню з вибором мови інтерфейсу</p>
                    <p>- exit - закриває все IDE</p>
                    <hr></hr>
                    {/* Обрати мову */}
                    <p>Обрати мову</p>
                    <Form.Select aria-label="Select Language" onChange={handleLanguageSelectChange} value={selectedLanguage}>
                        <option value="ua">Ukrainian</option>
                        <option value="en">English</option>
                    </Form.Select>
                    {/* Обрати тему */}
                    <p>Обрати тему</p>
                    <Form.Select aria-label="Select Theme" onChange={handleThemeChange} value={selectedTheme}>
                        <option value="azure-sky">Azure Sky</option>
                        <option value="dracula">Dracula</option>
                        <option value="lemon-sorbet">Lemon Sorbet</option>
                        <option value="monokai">Monokai</option>
                        <option value="one-dark-pro">One Dark Pro</option>
                        <option value="pink-rabbit">Pink Rabbit</option>
                        <option value="solarized-dark">Solarized Dark</option>
                        <option value="solarized-light">Solarized Light</option>
                        <option value="standard-dark">Standard Dark</option>
                        <option value="standard-light">Standard Light</option>
                        <option value="tomorrow">Tomorrow</option>
                        <option value="vanilla-cream">Vanilla Cream</option>
                    </Form.Select>
                    {/* Автозбереження */}
                    <p>Автозбереження</p>
                    <Form.Select aria-label="Select test" onChange={handleAutosaveSelectChange} value={autosaveInterval}>
                        <option value="0">Ніколи</option>
                        <option value="1">Хвилина</option>
                        <option value="5">П'ять</option>
                        <option value="10">Десять</option>
                    </Form.Select>
                    {/* Test */}
                    <p>test</p>
                    <Form.Select aria-label="Select test" onChange={handleTest} value={selectedTest}>
                        <option value="0">Ніколи</option>
                        <option value="1">Хвилина</option>
                        <option value="5">П'ять</option>
                        <option value="10">Десять</option>
                    </Form.Select>

                </div>
            </Row>
        </Container>
    );
}
