import React, { useState } from 'react';
import '../Standard-Component-style.css';
import './Terminal.css';

export default function Terminal() {
    const [inputValue, setInputValue] = useState('');
    const [outputValues, setOutputValues] = useState([]);

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            console.log('Enter pressed');
            const newOutput = inputValue; // Записати введену команду
            // Тут викликати функцію для виконання команди і отримання результату
            setOutputValues([...outputValues, newOutput]); // Додати введену команду до стану
            setInputValue(''); // Очистити поле вводу
        }
    };

    return (
        <div className='terminal-container resizable'>
            <div>
                <h5>Terminal</h5>
            </div>
            <div className='terminal-content'>
                {outputValues.map((output, index) => (
                    <div key={index} className='output'>
                        <span>{output}</span>
                    </div>
                ))}
                <div className='input-wrapper'>
                    <span>₴ </span>
                    <input
                        type='text'
                        placeholder='...'
                        value={inputValue}
                        onChange={handleInputChange}
                        onKeyPress={handleKeyPress}
                    />
                </div>
            </div>
        </div>
    );
}
