import React, { useState, useEffect } from 'react';
import Top_Bar_Menu from './Components/Top_Bar_Menu_Component/Top_Bar_Menu';
import Code_Editor from './Components/Code_Editor_Component/Code_Editor';
import File_Explorer from './Components/File_Explorer_Component/File_Explorer';
import Help from './Components/Help_Component/Help';
import Terminal from './Components/Terminal_component/Terminal';
import './App.css';

function App() {
  const [terminalVisible, setTerminalVisible] = useState(false);

  const toggleTerminal = () => {
    setTerminalVisible(!terminalVisible);
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.ctrlKey && event.key === '`') {
        event.preventDefault(); // Забороняємо браузеру виконувати дії за замовчуванням (наприклад, відкривати консоль)
        toggleTerminal();
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [toggleTerminal, terminalVisible]); // Додаємо toggleTerminal і terminalVisible до залежностей, щоб вони перевизначалися при їх зміні

  return (
    <div className="App">
      <Top_Bar_Menu toggleTerminal={toggleTerminal} />
      <Code_Editor />
      {/* <File_Explorer /> */}
      {terminalVisible && <Terminal />}
    </div>
  );
}

export default App;
