import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import '../Standard-Component-style.css';
import './Code_Editor.css';

export default function Code_Editor() {
    const [text, setText] = useState('');

    useEffect(() => {
        highlightCode();
    }, [text]);

    const highlightCode = () => {
        const codeDiv = document.getElementById('code-div');
        const highlightedCode = text.replace(/(функція|друк|змінна|якщо|інакше_якщо|інакше|поки|повернути|кінець)/g, '<span class="keyword">$1</span>');
        codeDiv.innerHTML = highlightedCode;
    };

    const handleInputChange = (event) => {
        setText(event.target.innerText);
    };

    return (
        <Container className='code-editor-container mar-24'>
            <div id="line-numbers" className="code-lines"></div>
            <div id="code-div" contentEditable="true" value={text} onInput={handleInputChange} className='code-editor'></div>
        </Container>
    );
}
