import React, { useState, useEffect } from 'react';
import { invoke } from '@tauri-apps/api/tauri';
import { save, open } from '@tauri-apps/api/dialog';
import { readTextFile } from '@tauri-apps/api/fs';
import TopBarMenu from './Components/Top_Bar_Menu_Component/Top_Bar_Menu';
import StartScreen from './Components/Start_Screen_Component/StartScreen';
import CodeEditor from './Components/Code_Editor_Component/Code_Editor';
import Help from './Components/Help_Component/Help';
import Terminal from './Components/Terminal_component/Terminal';
import './App.css';

function App() {
    const [terminalVisible, setTerminalVisible] = useState(false);
    const [helpVisible, setHelpVisible] = useState(false);
    const [startVisible, setStartVisible] = useState(true);
    const [filePath, setFilePath] = useState(null);
    const [programmingLanguage, setProgrammingLanguage] = useState('');

    // Функція для визначення мови програмування за шляхом файлу
    const getProgrammingLanguage = (filePath) => {
        const extension = filePath.split('.').pop();
        switch (extension) {
            case 'nl':
                return 'NikLang';
            case 'udav':
                return 'Udav';
            case 'based':
                return 'Based';
            default:
                return 'Other';
        }
    };

    // Функція для зміни стану відображення терміналу
    const toggleTerminal = () => {
        setTerminalVisible(!terminalVisible);
    };

    // Функція для зміни стану відображення довідки
    const toggleHelp = () => {
        setHelpVisible(!helpVisible);
    };

    // Прив'язка відкриття та закриття терміналу на Ctrl + `
    useEffect(() => {
        const handleKeyDown = (event) => {
            if (event.ctrlKey && event.key === '`') {
                event.preventDefault();
                toggleTerminal();
            }
        };
        window.addEventListener('keydown', handleKeyDown);
        return () => { window.removeEventListener('keydown', handleKeyDown); };
    }, [toggleTerminal]);

    // Функція для збереження вмісту файлу
    const saveFileContents = async (fileContent, savePath = filePath) => {};

    // Функція для створення нового
    const newFileContents = async (savePath = filePath) => {};

    // Функція відкриття файлу
    // const openFileContent = async () => {
    //     try
    //     {
    //         const selectedPath = await open({
    //             multiple: false,
    //             title: "Open file",
    //             filters: [
    //                 { name: 'Text File', extensions: ['**']},
    //                 { name: 'Niklang.nl', extensions: ['nl']},
    //                 { name: 'Based.based', extensions: ['based']},
    //                 { name: 'Udav.udav', extensions: ['udav']},
    //             ]
    //         });
    //         if(!selectedPath) return;
    //         console.log(selectedPath);
    //         const fileContent = await readTextFile(selectedPath);
    //         console.log(fileContent);
    //         const textarea = document.getElementById('textarea');
    //         if(textarea.value != '')
    //         {
    //             textarea.value = '';
    //         }
    //         textarea.value = fileContent;
    //         setFilePath(selectedPath);
    //         const language = getProgrammingLanguage(selectedPath); // Визначення мови програмування
    //         setProgrammingLanguage(language); // Встановлення мови програмування
    //         // Оновити lineNumber
    //         if (selectedPath) {
    //             setStartVisible(false);
    //         } else {
    //             setStartVisible(true);
    //         }
    //     } catch(err)
    //     {
    //         console.error(err);
    //     }
    // }
    // const openFileContent = async () => {
    //     try {
    //         const selectedPath = await open({
    //             multiple: false,
    //             title: "Open file",
    //             filters: [
    //                 { name: 'Text File', extensions: ['**']},
    //                 { name: 'Niklang.nl', extensions: ['nl']},
    //                 { name: 'Based.based', extensions: ['based']},
    //                 { name: 'Udav.udav', extensions: ['udav']},
    //             ]
    //         });
    //         if (!selectedPath) return;
    //         const fileContent = await readTextFile(selectedPath);
    //         setFilePath(selectedPath);
    //         const language = getProgrammingLanguage(selectedPath); // Визначення мови програмування
    //         setProgrammingLanguage(language); // Встановлення мови програмування
    //         const textarea = document.getElementById('textarea');
    //         textarea.value = fileContent; // Встановлення значення textarea
    //         // Оновити lineNumber
    //         if (selectedPath) {
    //             setStartVisible(false);
    //         } else {
    //             setStartVisible(true);
    //         }
    //     } catch (err) {
    //         console.error(err);
    //     }
    // }  
    const openFileContent = () => {
        const selectedPath = open({
            multiple: false,
            title: "Open file",
            filters: [
                { name: 'Text File', extensions: ['**']},
                { name: 'Niklang.nl', extensions: ['nl']},
                { name: 'Based.based', extensions: ['based']},
                { name: 'Udav.udav', extensions: ['udav']},
            ]
        }).then(selectedPath => {
            if (!selectedPath) return;
            return readTextFile(selectedPath);
        }).then(fileContent => {
            if (!fileContent) return;
            setFilePath(selectedPath);
            const language = getProgrammingLanguage(selectedPath);
            setProgrammingLanguage(language);
            const textarea = document.getElementById('textarea');
            textarea.value = fileContent;
            setStartVisible(false);
        }).catch(err => {
            console.error(err);
        });
    }      


    return (
        <div className="App">
            <TopBarMenu toggleTerminal={toggleTerminal} toggleHelp={toggleHelp} onSave={saveFileContents} onOpen={openFileContent} />
            <CodeEditor programming_language={programmingLanguage} />
            {startVisible && <StartScreen onNew={newFileContents} onOpen={openFileContent} />}
            {terminalVisible && <Terminal />}
            {helpVisible && <Help onClose={toggleHelp} />}
        </div>
    );
}

export default App;
