import React, { useState, useRef, useEffect } from "react";
import "./Terminal.css";

export default function Terminal({ visible }) {
  const [inputValue, setInputValue] = useState("");
  const [outputValues, setOutputValues] = useState([]);
  const outputWrapperRef = useRef(null);
  const terminalRef = useRef(null);
  const [resizing, setResizing] = useState(false);

  const handleMouseDown = (event) => {
    setResizing(true);
    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
  };

  const handleMouseMove = (event) => {
    if (resizing) {
      const terminalWidth = window.innerWidth - event.clientX;
      terminalRef.current.style.width = `${terminalWidth}px`;
    }
  };

  const handleMouseUp = () => {
    setResizing(false);
    document.removeEventListener("mousemove", handleMouseMove);
    document.removeEventListener("mouseup", handleMouseUp);
  };

  useEffect(() => {
    scrollToBottom(); // Прокручуємо до нижнього краю після кожного оновлення вмісту
  }, [outputValues]);

  const scrollToBottom = () => {
    if (outputWrapperRef.current) {
      outputWrapperRef.current.scrollTop =
        outputWrapperRef.current.scrollHeight;
    }
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      console.log("Enter pressed");
      const newOutput = inputValue; // Записати введену команду
      // Тут викликати функцію для виконання команди і отримання результату
      setOutputValues([
        ...outputValues,
        { command: inputValue, result: inputValue },
      ]); // Додати введену команду та результат до стану
      setInputValue(""); // Очистити поле вводу
    }
  };

  return (
    <div
      className={`terminal-container ${visible ? "visible" : "hidden"}`}
      ref={terminalRef}
    >
      <div className="terminal-header">
        <h5>Terminal</h5>
        <div
          className="terminal-resize-handle"
          onMouseDown={handleMouseDown}
        ></div>
      </div>
      <div className="terminal-content">
        <div className="output-wrapper" ref={outputWrapperRef}>
          {outputValues.map((output, index) => (
            <div key={index} className="output">
              <div>{"> " + output.command}</div>
              <div>{output.result}</div>
            </div>
          ))}
        </div>
        <div className="input-wrapper">
          <span>₴ </span>
          <input
            type="text"
            placeholder="..."
            value={inputValue}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
          />
        </div>
      </div>
    </div>
  );
}
