import React, { useState } from 'react';
import Top_Bar_Menu from './Components/Top_Bar_Menu_Component/Top_Bar_Menu';
import Code_Editor from './Components/Code_Editor_Component/Code_Editor';
import File_Explorer from './Components/File_Explorer_Component/File_Explorer';
import Help from './Components/Help_Component/Help';
import Terminal from './Components/Terminal_component/Terminal';
import './App.css';

function App() {
    const [terminalVisible, setTerminalVisible] = useState(false);
    const [helpVisible, setHelpVisible] = useState(false);

    const toggleTerminal = () => {
        setTerminalVisible(!terminalVisible);
    };

    const toggleHelp = () => {
        setHelpVisible(!helpVisible);
    };

    const closeHelp = () => {
        setHelpVisible(false);
    };

    return (
        <div className="App">
            <Top_Bar_Menu toggleTerminal={toggleTerminal} toggleHelp={toggleHelp} />
            <Code_Editor />
            {/* <File_Explorer /> */}
            {terminalVisible && <Terminal />}
            {helpVisible && <Help onClose={closeHelp} />}
        </div>
    );
}

export default App;
