import React, { useState, useEffect } from 'react';
import { Container, Row } from 'react-bootstrap';
import Dropdown from 'react-bootstrap/Dropdown';
import Form from 'react-bootstrap/Form';
import { useTranslation } from 'react-i18next';
import '../Standard-Component-style.css';
import './Help.css';

export default function Help({ onClose }) {
    const { t, i18n } = useTranslation("global");
    const [selectedLanguage, setSelectedLanguage] = useState(''); // початкове значення мови
    const [themes, setThemes] = useState([]); // стан для збереження списку тем

    useEffect(() => {
        const storedLang = localStorage.getItem('language');
        if (storedLang) {
            handleLanguageChange(storedLang);
        } else {
            // Встановлення української мови за замовчуванням
            handleLanguageChange('ua');
        }
    }, []);

    const handleLanguageChange = (lang) => {
        i18n.changeLanguage(lang);
        setSelectedLanguage(lang);
        localStorage.setItem('language', lang);
    };

    // Отримати список файлів тем з Rust
    const fetchThemes = async () => {
        try {
            const themes = await window.tauri.get_theme_files();
            setThemes(themes);
        } catch (error) {
            console.error('Error fetching themes:', error);
        }
    };

    useEffect(() => {
        // Викликати метод отримання файлів тем під час завантаження компонента
        fetchThemes();
    }, []);

    // Оголосіть стан для відстеження завантаження тем
    const [themesLoaded, setThemesLoaded] = useState(false);

    // Оновіть стан, коли теми будуть завантажені
    useEffect(() => {
        if (themes.length > 0) {
            setThemesLoaded(true);
        }
    }, [themes]);

    // Відобразіть вміст тільки після завантаження тем
    return (
        <Container className='help-container'>
            <Row className='help-close-button'>
                <button onClick={onClose}><b>X</b></button>
            </Row>
            <Row className='help-content-container'>
                <div className='help-content'>
                    <h1>NUB IDE</h1>
                    <p>Це середовище розробки створене спеціально для мов програмування NukLang, Удав та Based</p>
                    <p>Його основною ідеєю є робота через термінал, але не повна</p>
                    <p>Інтерпретатори мов та компілятори були встановлені автоматично</p>
                    <p>Оскільки мови не сильно працюють із сторонніми файлами, то робота у даному редакторі коду також здійснюється у межах одного файлу</p>
                    <hr></hr>
                    <p>Ide має такі команди для терміналу:</p>
                    <p>- cd [path]</p>
                    <p>- ls </p>
                    <p>- pwd</p>
                    <p>- open - відкриває файл</p>
                    <p>- save - зберігає файл</p>
                    <p>- save as - зберегти файл як</p>
                    <p>- translate - викликає меню з вибором мови інтерфейсу</p>
                    <p>- exit - закриває все IDE</p>
                    <hr></hr>
                    {/* Обрати мову */}
                    <Dropdown className='language-dropdown'>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            {t("help.select_language")}
                        </Dropdown.Toggle>
                        <Dropdown.Menu className='language-dropdown-content'>
                            <Dropdown.Item onClick={() => handleLanguageChange("ua")}>Ukrainian</Dropdown.Item>
                            <Dropdown.Item onClick={() => handleLanguageChange("en")}>English</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                    {/* Обрати тему */}
                    {themesLoaded && (
                        <Form.Select aria-label="Default select example">
                            <option>Choose a theme</option>
                            {themes.map((theme, index) => (
                                <option key={index} value={theme}>{theme}</option>
                            ))}
                        </Form.Select>
                    )}
                    <button>Створити власну тему</button>
                </div>
            </Row>
        </Container>
    );
}
