import React, { useState } from 'react';
import { invoke } from '@tauri-apps/api/tauri';
import { save } from '@tauri-apps/api/dialog';
import TopBarMenu from './Components/Top_Bar_Menu_Component/Top_Bar_Menu';
import CodeEditor from './Components/Code_Editor_Component/Code_Editor';
import Help from './Components/Help_Component/Help';
import Terminal from './Components/Terminal_component/Terminal';
import './App.css';

function App() {
    // Стан для відображення/приховування терміналу та довідки
    const [terminalVisible, setTerminalVisible] = useState(false);
    const [helpVisible, setHelpVisible] = useState(false);

    // Функція для зміни стану відображення терміналу
    const toggleTerminal = () => {
        setTerminalVisible(!terminalVisible);
    };

    // Функція для зміни стану відображення довідки
    const toggleHelp = () => {
        setHelpVisible(!helpVisible);
    };

    // Функція для закриття довідки
    const closeHelp = () => {
        setHelpVisible(false);
    };

    // Функція для збереження вмісту файлу
    const saveFileContents = async (fileContent) => {
        try {
            // Перевіряємо, чи вже існує шлях до файлу
            if (filePath) {
                // Якщо так, зберігаємо файл за цим шляхом
                await invoke("save_file", { path: filePath, contents: fileContent });
            } else {
                // Якщо шляху немає, відкриваємо діалогове вікно для збереження файлу
                const savePath = await save({
                    filters: [
                        { name: 'Niklang.nl', extensions: ['nl']},
                        { name: 'Based.based', extensions: ['based']},
                        { name: 'Udav.udav', extensions: ['udav']},
                        { name: 'Other', extensions: ['**']}
                    ]
                });
                // Якщо користувач вибрав шлях, зберігаємо його та файл
                if (savePath) {
                    setFilePath(savePath);
                    await invoke("save_file", { path: savePath, contents: fileContent });
                }
            }
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div className="App">
            {/* Передаємо функцію toggleTerminal, toggleHelp та saveFileContents компоненту TopBarMenu */}
            <TopBarMenu toggleTerminal={toggleTerminal} toggleHelp={toggleHelp} onSave={saveFileContents} />
            {/* Передаємо функцію saveFileContents компоненту CodeEditor */}
            <CodeEditor onSave={saveFileContents} />
            {/* Умовний рендерінг компонента Terminal */}
            {terminalVisible && <Terminal />}
            {/* Умовний рендерінг компонента Help */}
            {helpVisible && <Help onClose={closeHelp} />}
        </div>
    );
}

export default App;
