import React, { useState, useRef, useEffect } from "react";
import "./Terminal.css";

export default function Terminal({ visible }) {
  const [inputValue, setInputValue] = useState("");
  const [outputValues, setOutputValues] = useState([]);
  const [height, setHeight] = useState(200); // Додано стейт для висоти
  const outputWrapperRef = useRef(null);

  const scrollToBottom = () => {
    if (outputWrapperRef.current) {
      outputWrapperRef.current.scrollTop =
        outputWrapperRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [outputValues]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      console.log("Enter pressed");
      const newOutput = inputValue;
      setOutputValues([
        ...outputValues,
        { command: inputValue, result: inputValue },
      ]);
      setInputValue("");
    }
  };

  const handleMouseDown = (event) => {
    const startY = event.clientY;
    const startHeight = height;

    const handleMouseMove = (event) => {
      const deltaY = startY - event.clientY;
      setHeight(startHeight + deltaY);
    };

    const handleMouseUp = () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };

    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
  };

  return (
    <div
      className={`terminal-container ${visible ? "visible" : "hidden"}`}
      style={{ height: `${height}px` }} // Додаємо висоту в стилі компонента
    >
      <div className="terminal-header" onMouseDown={handleMouseDown}>
        <h5>Terminal</h5>
      </div>
      <div className="terminal-content">
        <div className="output-wrapper" ref={outputWrapperRef}>
          {outputValues.map((output, index) => (
            <div key={index} className="output">
              <div>{"> " + output.command}</div>
              <div>{output.result}</div>
            </div>
          ))}
        </div>
        <div className="input-wrapper">
          <span>₴ </span>
          <input
            type="text"
            placeholder="..."
            value={inputValue}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
          />
        </div>
      </div>
    </div>
  );
}
