import { Container, Row } from 'react-bootstrap';
// import { open, invoke } from '@tauri-apps/api/tauri';
// import fs from '@tauri-apps/api/fs'; // Імпорт fs з Tauri API
import { open } from '@tauri-apps/api/dialog';
import { invoke } from '@tauri-apps/api';
import { fs } from '@tauri-apps/api';
import '../Standard-Component-style.css';
import './Top_Bar_Menu.css';

export default function Top_Bar_Menu({ toggleTerminal, toggleHelp }) {
    const exitApp = async () => {
        await invoke('exit');
    };

    const handleOpenFile = async () => {
        const result = await open({
            directory: "~", // Встановлюємо початкову теку відкриття файлу
            multiple: false, // Дозволяємо відкриття лише одного файлу
            filter: "All Files" // Фільтр на всі файли
        });

        if (!result) return; // Якщо користувач не обрав файл, вийти

        const filePath = result.payload[0].path;
        try {
            const fileContent = await fs.readTextFile(filePath); // Читаємо вміст файлу
            // Використовуйте fileContent для оновлення textarea або відображення вмісту файлу
            console.log(fileContent); // Виведення змісту файлу в консоль
        } catch (error) {
            console.error("Error reading the file:", error);
        }
    };

    return (
        <Container className='top-bar-container'>
            <Row className='top-bar-content'>
                <button id='openButton' onClick={handleOpenFile}>Open</button>
                <button id='saveButton'>Save</button>
                <button id='saveAsButton'>Save as</button>
                <button id='termButton' onClick={toggleTerminal}>Terminal</button>
                <button id='helpButton' onClick={toggleHelp}>Help</button>
                <button id='exitButton' onClick={exitApp}>Exit</button>
            </Row>
        </Container>
    );
}
