import React, { useState, useEffect, useRef } from 'react';
import { Container } from 'react-bootstrap';
import './Code_Editor.css';

export default function CodeEditor({ programming_language, fileContent }) {
    const [text, setText] = useState('');
    const [history, setHistory] = useState([]);
    const [currentStep, setCurrentStep] = useState(-1);
    const contentEditableRef = useRef(null);

    // Ключові слова мов програмування
    const NikLangKeyword = ['друк', 'ввід', 'ввід_числа', 'якщо', 'інакше', 'інакше_якщо', 'поки', 'для_кожного', 'повернути', 'функція', 'змінна', 'ціле', 'дійсне', 'рядок', 'логічне', 'символ', 'кінець'];
    const UdavKeywords = ['друк', 'ввід', 'якщо', 'інакше', 'інакшеЯкщо', 'правда', 'брехня', 'або', 'не', 'також', 'для', 'поки', 'функція', 'припинити', 'продовжити', 'повернути', 'пропуск', 'клас', 'як', 'від', 'отримати', 'заочно', 'жодний', 'крім', 'належить', 'підняти', 'остаточно', 'це', 'лямбда', 'спробувати', 'неЛокально', 'утверджувати', 'видалити', 'глобально', 'із', 'асинхронний', 'здобути', 'число', 'дріб', 'рядок', 'границях', 'своє'];
    const BasedKeywords = ['друк', 'ввід', 'ввід_числа', 'якщо', 'інакше', 'інакше_якщо', 'поки', 'для_кожного', 'повернути', 'функція', 'змінна', 'ціле', 'дійсне', 'рядок', 'логічне', 'символ', 'кінець'];

    // Відміна дії
    const undo = () => {
        if (currentStep > 0) {
            setText(history[currentStep - 1]);
            setCurrentStep(currentStep - 1);
        }
    };

    // На дію вперед
    const redo = () => {
        if (currentStep < history.length - 1) {
            setText(history[currentStep + 1]);
            setCurrentStep(currentStep + 1);
        }
    };

    // Додавання табуляції
    const tabulation = () => {
        const contentEditable = contentEditableRef.current;
        document.execCommand('insertText', false, '\t');
        setText(contentEditable.textContent);
    };        

    // Створення коментаря
    const createOrRemoveComment = () => {
        let commentSymbol = ""; 

        switch (programming_language) {
            case "NikLang":
                commentSymbol = "// "; 
                break;
            case "Udav":
            case "Based":
                commentSymbol = "# ";
                break;
            default:
                commentSymbol = "";
                break;
        }

        const contentEditable = contentEditableRef.current;
        const selection = window.getSelection();
        const range = selection.getRangeAt(0);
        const text = contentEditable.textContent;
        const start = text.substring(0, range.startOffset);
        const end = text.substring(range.endOffset);

        let newText;
        // Видаляємо коментар
        if (range.toString().trim().startsWith(commentSymbol.trim())) {
            newText = start + end.substring(commentSymbol.length);
        } 
        // Додаємо коментар
        else {
            newText = start + commentSymbol + range.toString() + end;
        }

        setText(newText);
        contentEditable.textContent = newText;
        range.collapse(false);
        selection.removeAllRanges();
        selection.addRange(range);
    };

    // Натискання на Ctrl + z та Ctrl + y
    useEffect(() => {
        const handleUndoRedo = (event) => {
            if (event.ctrlKey && event.key === 'z') {
                event.preventDefault();
                undo();
            } else if (event.ctrlKey && event.key === 'y') {
                event.preventDefault();
                redo();
            }
        };

        window.addEventListener('keydown', handleUndoRedo);

        return () => {
            window.removeEventListener('keydown', handleUndoRedo);
        };
    }, [undo, redo]);

    // Натискання на Tab
    useEffect(() => {
        const handleTabulation = (event) => {
            if (event.key === 'Tab') {
                event.preventDefault();
                tabulation();
            }
        };

        window.addEventListener('keydown', handleTabulation);

        return () => {
            window.removeEventListener('keydown', handleTabulation);
        };
    }, [tabulation]);

    // Додавання коментарів
    useEffect(() => {
        const handleComment = (event) => {
            if (event.ctrlKey && event.key === '/') {
                event.preventDefault();
                createOrRemoveComment();
            }
        };

        window.addEventListener('keydown', handleComment);

        return () => {
            window.removeEventListener('keydown', handleComment);
        };
    }, [createOrRemoveComment]);

    // Відкриття файлу
    useEffect(() => {
        if (fileContent) {
            if(fileContent == "new_file")
            {
                setText("");
            }
            else
            {
                setText(fileContent);
            }
        }
    }, [fileContent]);

    useEffect(() => {
        const contentEditable = contentEditableRef.current;
        const lineNumbersEle = document.getElementById('line-numbers');

        const displayLineNumbers = () => {
            const lines = text.split('\n');
            lineNumbersEle.innerHTML = Array.from({
                length: lines.length,
            }, (_, i) => `<div>${i + 1}</div>`).join('');
        };

        displayLineNumbers();

        const handleInput = () => {
            setText(contentEditable.textContent);
        };                

        contentEditable.addEventListener('input', handleInput);

        return () => {
            contentEditable.removeEventListener('input', handleInput);
        };
    }, [text]);

    const handleChange = () => {
        const contentEditable = contentEditableRef.current;
        const newHistory = [...history.slice(0, currentStep + 1), contentEditable.textContent];
        setHistory(newHistory);
        setCurrentStep(newHistory.length - 1);
    };

    return (
        <Container className='code-editor-container'>
            <div id="line-numbers" className="code-lines"></div>
            <div
                ref={contentEditableRef}
                id="contenteditable"
                contentEditable
                aria-multiline
                onInput={handleChange}
                className='code-editor'>
                {text}
            </div>
        </Container>
    );
}
