import React, { useState, useRef, useEffect } from 'react';
import '../Standard-Component-style.css';
import './Terminal.css';

export default function Terminal() {
    const [inputValue, setInputValue] = useState('');
    const [outputValues, setOutputValues] = useState([]);
    const terminalContentRef = useRef(null);
    const inputWrapperRef = useRef(null);

    useEffect(() => {
        // Прокручуємо див до нижнього краю при кожному оновленні вмісту
        if (terminalContentRef.current) {
            terminalContentRef.current.scrollTop = terminalContentRef.current.scrollHeight;
        }
        // Прокручуємо input-wrapper до нижнього краю
        if (inputWrapperRef.current) {
            inputWrapperRef.current.scrollIntoView({ behavior: "smooth", block: "end" });
        }
    }, [outputValues]);

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            console.log('Enter pressed');
            const newOutput = inputValue; // Записати введену команду
            // Тут викликати функцію для виконання команди і отримання результату
            setOutputValues([...outputValues, { command: inputValue, result: inputValue }]); // Додати введену команду та результат до стану
            setInputValue(''); // Очистити поле вводу
        }
    };

    return (
        <div className='terminal-container resizable'>
            <div className='terminal-header'>
                <h5>Terminal</h5>
            </div>
            <div className='terminal-content' ref={terminalContentRef}>
                <div className='output-wrapper'>
                    {outputValues.map((output, index) => (
                        <div key={index} className='output'>
                            <div>{'> ' + output.command}</div>
                            <div>{output.result}</div>
                        </div>
                    ))}
                </div>
                <div className='input-wrapper' ref={inputWrapperRef}>
                    <span>₴ </span>
                    <input
                        type='text'
                        placeholder='...'
                        value={inputValue}
                        onChange={handleInputChange}
                        onKeyPress={handleKeyPress}
                    />
                </div>
            </div>
        </div>
    );
}
