import React, { useState } from 'react';
import { Container, Row, Dropdown } from 'react-bootstrap';
import '../Standard-Component-style.css';
import './Help.css';

export default function Help({ onClose }) {
    const [selectedLanguage, setSelectedLanguage] = useState('Мова'); // початкове значення мови

    const handleLanguageChange = (language) => {
        setSelectedLanguage(language);
        // Тут ви можете додати логіку для зміни мови у вашій програмі
    };

    return (
        <Container className='help-container'>
            <Row className='help-close-button'>
                <button onClick={onClose}><b>X</b></button>
            </Row>
            <Row className='help-content-container'>
                <div className='help-content'>
                    <h1>NUB IDE</h1>
                    <p>Це середовище розробки створене спеціально для мов програмування NukLang, Удав та Based</p>
                    <p>Ide має такі команди для терміналу:</p>
                    <p>- open - відкриває файл</p>
                    <p>- save - зберігає файл</p>
                    <p>- save as - зберегти файл як</p>
                    <p>- translate - викликає меню з вибором мови інтерфейсу</p>
                    <p>- close - закриває все IDE</p>
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            {selectedLanguage}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item onSelect={() => handleLanguageChange('English')}>English</Dropdown.Item>
                            <Dropdown.Item onSelect={() => handleLanguageChange('Українська')}>Українська</Dropdown.Item>
                            {/* Тут можна додати інші мови */}
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
            </Row>
        </Container>
    );
}
