import { useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { Container, Row } from 'react-bootstrap';
import { exit } from '@tauri-apps/api/process';
import '../Standard-Component-style.css';
import './Top_Bar_Menu.css';

export default function TopBarMenu({ toggleTerminal, toggleHelp, onSave }) {
    const { t, i18n } = useTranslation("global");

    const exitApp = async () => {
        await exit(0);
    };

    const handleSave = () => {
        const textarea = document.getElementById('textarea');
        const fileContent = textarea.value;
        onSave(fileContent);
    };

    const handleSaveAs = async () => {
        const textarea = document.getElementById('textarea');
        const fileContent = textarea.value;
        onSave(fileContent, null);
    };

    useEffect(() => {
        const handleKeyDown = (event) => {
            if (event.ctrlKey && event.key === 's') {
                event.preventDefault();
                const textarea = document.getElementById('textarea');
                const fileContent = textarea.value;
                onSave(fileContent);
            }
            if (event.ctrlKey && event.shiftKey && event.key === 'S') {
                event.preventDefault();
                const textarea = document.getElementById('textarea');
                const fileContent = textarea.value;
                onSave(fileContent, null);
            }
        };

        window.addEventListener('keydown', handleKeyDown);

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
        };
    }, [onSave]);

    return (
        <Container className='top-bar-container'>
            <Row className='top-bar-content'>
                <button id='openButton'>{t("header.open")}</button>
                <button id='saveButton' onClick={handleSave}>Save</button>
                <button id='saveAsButton' onClick={handleSaveAs}>Save as</button>
                <button id='termButton' onClick={toggleTerminal}>Terminal</button>
                <button id='helpButton' onClick={toggleHelp}>Help</button>
                <button id='exitButton' onClick={exitApp}>Exit</button>
            </Row>
        </Container>
    );
}
