import React, { useState, useEffect, useRef } from 'react';
import { Container } from 'react-bootstrap';
import '../Standard-Component-style.css';
import './Code_Editor.css';

export default function CodeEditor() {
    const [text, setText] = useState('');
    const lineNumbersRef = useRef(null);

    useEffect(() => {
        const displayLineNumbers = () => {
            const lines = text.split('\n');
            const lineNumbersHtml = lines.map((_, i) => `<div>${i + 1}</div>`).join('');
            lineNumbersRef.current.innerHTML = lineNumbersHtml || '<div>1</div>';
        }

        displayLineNumbers();
    }, [text]);

    const handleInputChange = (event) => {
        setText(event.target.value);
    };

    return (
        <Container className='code-editor-container'>
            <div ref={lineNumbersRef} className="code-lines"></div>
            <textarea value={text} onChange={handleInputChange} className='code-editor'></textarea>
        </Container>
    );
}
