import React, { useState, useRef, useEffect } from "react";
import { useTranslation } from "react-i18next";
import "./Terminal.css";

const initialInputValue = "";
const initialOutputValues = []; // Початковий стан для outputValues

export default function Terminal({ visible }) {
  const [inputValue, setInputValue] = useState("");
  const [outputValues, setOutputValues] = useState(initialOutputValues);
  const outputWrapperRef = useRef(null);

  // Локалізація програми
  const { t, i18n } = useTranslation("global");

  useEffect(() => {
    scrollToBottom();
  }, [outputValues]);

  const scrollToBottom = () => {
    if (outputWrapperRef.current) {
      outputWrapperRef.current.scrollTop =
        outputWrapperRef.current.scrollHeight;
    }
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      console.log("Enter pressed");
      const command = inputValue.trim();
      let result;

      if (command === "new" || command === "новий") {
        result = "Новий файл!";
      } else if (command === "clear" || command === "очистити") {
        result = "Термінал очищено!";
        setOutputValues(initialOutputValues);
        setInputValue(initialInputValue);
        return;
      } else {
        result = "Невідома команда";
      }

      setOutputValues([...outputValues, { command: command, result: result }]);
      setInputValue("");
    }
  };

  return (
    <div className={`terminal-container ${visible ? "visible" : "hidden"}`}>
      <div className="terminal-header">
        <h5>Terminal</h5>
      </div>
      <div className="terminal-content">
        <div className="output-wrapper" ref={outputWrapperRef}>
          {outputValues.map((output, index) => (
            <div key={index} className="output">
              <div>{"> " + output.command}</div>
              <div>{output.result}</div>
            </div>
          ))}
        </div>
        <div className="input-wrapper">
          <span>₴ </span>
          <input
            type="text"
            placeholder="..."
            value={inputValue}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
          />
        </div>
      </div>
    </div>
  );
}
