import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import '../Standard-Component-style.css';
import './Terminal.css';

export default function Terminal() {
    const [input, setInput] = useState('');
    const [output, setOutput] = useState('');

    const handleInputChange = (e) => {
      setInput(e.target.value);
    };

    const handleCommandSubmit = (e) => {
      e.preventDefault();
      // Виконання команди
      executeCommand(input);
      // Очищення поля вводу
      setInput('');
    };

    const executeCommand = (command) => {
      // Тут ви можете виконати команду та отримати результат
      // Наприклад, використовуючи eval() або викликаючи відповідні функції
      let result = ''; // Результат виконання команди
      setOutput(output + `\n> ${command}\n${result}\n`);
    };

    return (
      <div className="terminal">
        <div className="output">
          <pre>{output}</pre>
        </div>
        <form onSubmit={handleCommandSubmit}>
          <input
            type="text"
            value={input}
            onChange={handleInputChange}
            placeholder="Введіть команду..."
          />
          <button type="submit">Виконати</button>
        </form>
      </div>
    );
}
