import { useEffect } from "react";
import { confirm } from "@tauri-apps/api/dialog";
import { useTranslation } from "react-i18next";
import { Container, Row } from "react-bootstrap";
import { exit } from "@tauri-apps/api/process";
// import "bootstrap/dist/css/bootstrap.min.css";
import { Dropdown } from "react-bootstrap";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "./Top_Bar_Menu.css";

export default function TopBarMenu({
  toggleTerminal,
  toggleHelp,
  onNew,
  onSave,
  onOpen,
}) {
  // Локалізація програми
  const { t, i18n } = useTranslation("global");

  const handleNew = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value != "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk == true) {
        handleSave();
        onNew();
      } else {
        onNew();
      }
    } else {
      onNew();
    }
  };

  // Отримання шляху файлу до файлу що відкрити його
  const handleOpen = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value != "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk == true) {
        handleSave();
        onOpen();
      } else {
        onOpen();
      }
    } else {
      onOpen();
    }
  };

  // Отримання тексту з textarea для збереження
  const handleSave = () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent);
  };

  // Отримання тексту з textarea для збереження як
  const handleSaveAs = async () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent, null);
  };

  // Прив'язка гарячих клавіш Ctrl+S та Ctrl+Shift+S
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.ctrlKey && event.key === "s") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent);
      }
      if (event.ctrlKey && event.shiftKey && event.key === "S") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent, null);
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [onSave]);

  // Закриття програми
  const exitApp = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value != "") {
      const saveAsk = await confirm(t("dialogue.exit_save"), "NUB IDE");
      if (saveAsk == true) {
        handleSave();
        await exit(0);
      } else {
        await exit(0);
      }
    } else {
      await exit(0);
    }
  };

  return (
    <Container className="top-bar-container">
      <Row className="top-bar-content">
        <button id="newButton" onClick={handleNew}>
          {t("header.new")}
        </button>
        <button id="openButton" onClick={handleOpen}>
          {t("header.open")}
        </button>
        <button id="saveButton" onClick={handleSave}>
          {t("header.save")}
        </button>
        <button id="saveAsButton" onClick={handleSaveAs}>
          {t("header.save_as")}
        </button>
        <button id="termButton" onClick={toggleTerminal}>
          {t("header.terminal")}
        </button>
        <button id="helpButton" onClick={toggleHelp}>
          {t("header.help")}
        </button>
        <button id="exitButton" onClick={exitApp}>
          {t("header.exit")}
        </button>
      </Row>
    </Container>
  );
}
