import React, { useState } from "react";
import { useEffect } from "react";
import { Button, Container, Row } from "react-bootstrap";
import { confirm } from "@tauri-apps/api/dialog";
import { useTranslation } from "react-i18next";
import { exit } from "@tauri-apps/api/process";
import "./Top_Bar_Menu.css";

export default function TopBarMenu({
  toggleTerminal,
  toggleHelp,
  onNew,
  onSave,
  onOpen,
}) {
  // Локалізація програми
  const { t, i18n } = useTranslation("global");

  const [fileMenuVisible, setFileMenuVisible] = useState(false);

  const toggleFileMenu = () => {
    setFileMenuVisible(!fileMenuVisible);
  };

  const handleNew = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk === true) {
        handleSave();
        onNew();
      } else {
        onNew();
      }
    } else {
      onNew();
    }
  };

  const handleOpen = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk === true) {
        handleSave();
        onOpen();
      } else {
        onOpen();
      }
    } else {
      onOpen();
    }
  };

  const handleSave = () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent);
  };

  const handleSaveAs = async () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent, null);
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.ctrlKey && event.key === "s") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent);
      }
      if (event.ctrlKey && event.shiftKey && event.key === "S") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent, null);
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [onSave]);

  const exitApp = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.exit_save"), "NUB IDE");
      if (saveAsk === true) {
        handleSave();
        await exit(0);
      } else {
        await exit(0);
      }
    } else {
      await exit(0);
    }
  };

  return (
    <Container className="top-bar-container">
      <Row className="top-bar-content">
        <div className="file-menu-wrapper">
          <Button id="fileButton" onClick={toggleFileMenu}>
            {t("header.file")}
          </Button>
          {fileMenuVisible && (
            <div className="file-menu">
              <Button onClick={handleNew}>{t("header.new")}</Button>
              <Button onClick={handleOpen}>{t("header.open")}</Button>
              <Button onClick={handleSave}>{t("header.save")}</Button>
              <Button onClick={handleSaveAs}>{t("header.save_as")}</Button>
            </div>
          )}
        </div>
        <Button id="termButton" onClick={toggleTerminal}>
          {t("header.terminal")}
        </Button>
        <Button id="helpButton" onClick={toggleHelp}>
          {t("header.help")}
        </Button>
        <Button id="exitButton" onClick={exitApp}>
          {t("header.exit")}
        </Button>
      </Row>
    </Container>
  );
}
