    _getSelectionEnd(): number {
      const el = ref[refKey];
      if (!el) return 0;
      const pos = el.selectionEnd!;
      // compensate selection range during compositioning
      if (!compositionEvent) return pos;
      return Math.min(pos, el.selectionStart! + compositionEvent.data.length);
