import { useEffect } from "react";
import { confirm } from "@tauri-apps/api/dialog";
import { useTranslation } from "react-i18next";
import { Container, Row, Button } from "react-bootstrap";
import { exit } from "@tauri-apps/api/process";
import "./Top_Bar_Menu.css";

export default function TopBarMenu({
  toggleTerminal,
  toggleHelp,
  onNew,
  onSave,
  onOpen,
}) {
  // Локалізація програми
  const { t, i18n } = useTranslation("global");

  const handleNew = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk) {
        handleSave();
      }
      onNew();
    } else {
      onNew();
    }
  };

  // Отримання шляху файлу до файлу що відкрити його
  const handleOpen = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk) {
        handleSave();
      }
      onOpen();
    } else {
      onOpen();
    }
  };

  // Отримання тексту з textarea для збереження
  const handleSave = () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent);
  };

  // Отримання тексту з textarea для збереження як
  const handleSaveAs = async () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent, null);
  };

  // Прив'язка гарячих клавіш Ctrl+S та Ctrl+Shift+S
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.ctrlKey && event.key === "s") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent);
      }
      if (event.ctrlKey && event.shiftKey && event.key === "S") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent, null);
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [onSave]);

  // Закриття програми
  const exitApp = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.exit_save"), "NUB IDE");
      if (saveAsk) {
        handleSave();
      }
    }
    await exit(0);
  };

  return (
    <Container className="top-bar-container">
      <Row className="top-bar-content">
        <Button onClick={handleNew}>{t("header.new")}</Button>
        <Button onClick={handleOpen}>{t("header.open")}</Button>
        <Button onClick={handleSave}>{t("header.save")}</Button>
        <Button onClick={handleSaveAs}>{t("header.save_as")}</Button>
        <Button onClick={toggleTerminal}>{t("header.terminal")}</Button>
        <Button onClick={toggleHelp}>{t("header.help")}</Button>
        <Button onClick={exitApp}>{t("header.exit")}</Button>
      </Row>
    </Container>
  );
}
