// import React, { useState, useEffect } from 'react';
import React, { useState, useRef, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import '../Standard-Component-style.css';
import './Terminal.css';

export default function Terminal() {
    const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const outputRef = useRef(null);

  useEffect(() => {
    // Прокрутка вниз, щоб бачити останні виводи
    outputRef.current.scrollTop = outputRef.current.scrollHeight;
  }, [output]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleCommandSubmit = (e) => {
    e.preventDefault();
    executeCommand(input);
    setInput('');
  };

  const executeCommand = (command) => {
    let result = ''; // Результат виконання команди
    // Можете використовувати тут функцію для виконання команди
    setOutput(output + `\n> ${command}\n${result}\n`);
  };

  return (
    <div className="terminal">
      <div className="terminal-header">
        <div className="circle red"></div>
        <div className="circle yellow"></div>
        <div className="circle green"></div>
      </div>
      <div className="output" ref={outputRef}>
        <pre>{output}</pre>
      </div>
      <form onSubmit={handleCommandSubmit}>
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          placeholder="Введіть команду..."
        />
      </form>
    </div>
  );
}
