import React, { useState, useEffect } from 'react';
import { open } from '@tauri-apps/api/dialog';
import { Container } from 'react-bootstrap';
import '../Standard-Component-style.css';
import './Code_Editor.css';

export default function CodeEditor() {
    const [text, setText] = useState('');

    useEffect(() => {
        const textarea = document.getElementById('textarea');
        const lineNumbersEle = document.getElementById('line-numbers');

        const displayLineNumbers = () => {
            const lines = text.split('\n');
            lineNumbersEle.innerHTML = Array.from({
                length: lines.length,
            }, (_, i) => `<div>${i + 1}</div>`).join('');
        }

        displayLineNumbers();
    }, [text]);

    const handleInputChange = (event) => {
        setText(event.target.value);
    };

    const handleOpen = async () => {
        try {
            const result = await open();
            if (!result || !result.path) return;
            const fileContent = await invoke("read_file", { path: result.path });
            onOpen(fileContent);
        } catch (error) {
            console.error('Error while opening file:', error);
        }
    };

    return (
        <Container className='code-editor-container'>
            <div id="line-numbers" className="code-lines"></div>
            <textarea id="textarea" value={text} onChange={handleInputChange} className='code-editor'></textarea>
        </Container>
    );
}
