import React, { useState, useEffect, useRef } from 'react';
import { Container } from 'react-bootstrap';
import './Code_Editor.css';

export default function CodeEditor({ programming_language }) {
    const [text, setText] = useState('');
    const [history, setHistory] = useState([]);
    const [currentStep, setCurrentStep] = useState(-1);
    const textareaRef = useRef(null);

    const moveCursor = () => {
        const textarea = textareaRef.current;
        const currentPosition = textarea.selectionStart;
        textarea.setSelectionRange(currentPosition - 1, currentPosition - 1);
        textarea.focus();
    };

    const undo = () => {
        if (currentStep > 0) {
            setText(history[currentStep - 1]);
            setCurrentStep(currentStep - 1);
        }
    };

    const redo = () => {
        if (currentStep < history.length - 1) {
            setText(history[currentStep + 1]);
            setCurrentStep(currentStep + 1);
        }
    };

    // Додавання табуляції
    const tabulation = () => {
        const textarea = textareaRef.current;
        const { selectionStart, selectionEnd, value } = textarea;
        const start = value.substring(0, selectionStart);
        const end = value.substring(selectionEnd);
        const tabbedText = start + '\t' + end;
        setText(tabbedText);
        const currentPosition = textarea.selectionStart;
        textarea.value = tabbedText;
        textarea.setSelectionRange(currentPosition + 1, currentPosition + 1);
        textarea.focus();
    };        

    // Створення коментаря
    const moveToLineStart = () => {
        const textarea = document.getElementById('textarea');
        const currentPosition = textarea.selectionStart;
        const textBeforeCursor = textarea.value.substring(0, currentPosition);
        const lastNewLineIndex = textBeforeCursor.lastIndexOf('\n');
        const newPosition = lastNewLineIndex === -1 ? 0 : lastNewLineIndex + 1;
        textarea.setSelectionRange(newPosition, newPosition);
        textarea.focus();
    };

    const createOrRemoveComment = () => {
        let commentSymbol = ""; 

        switch (programming_language) {
            case "NikLang":
                commentSymbol = "// "; 
                break;
            case "Udav":
            case "Based":
                commentSymbol = "# ";
                break;
            default:
                commentSymbol = "";
                break;
        }

        moveToLineStart();

        const textarea = document.getElementById('textarea');
        const currentPosition = textarea.selectionStart;
        const textAfterCursor = textarea.value.substring(currentPosition); 

        if (textAfterCursor.trim().startsWith(commentSymbol.trim())) {
            setText(textarea.value.substring(0, currentPosition) + textarea.value.substring(currentPosition + commentSymbol.length));
            textarea.setSelectionRange(currentPosition, currentPosition); 
        } else {
            const newText = textarea.value.substring(0, currentPosition) + commentSymbol + textarea.value.substring(currentPosition);
            setText(newText);
            textarea.setSelectionRange(currentPosition + commentSymbol.length, currentPosition + commentSymbol.length); 
        }

        textarea.focus();
    };

    useEffect(() => {
        const handleUndoRedo = (event) => {
            if (event.ctrlKey && event.key === 'z') {
                event.preventDefault();
                undo();
            } else if (event.ctrlKey && event.key === 'y') {
                event.preventDefault();
                redo();
            }
        };

        window.addEventListener('keydown', handleUndoRedo);

        return () => {
            window.removeEventListener('keydown', handleUndoRedo);
        };
    }, [undo, redo]);

    // Натискання на Tab
    useEffect(() => {
        const handleTabulation = (event) => {
            if (event.key === 'Tab') {
                event.preventDefault();
                tabulation();
            }
        };

        window.addEventListener('keydown', handleTabulation);

        return () => {
            window.removeEventListener('keydown', handleTabulation);
        };
    }, [tabulation]);

    useEffect(() => {
        const handleEnterPress = (event) => {
            if (event.key === "Enter") {
                const textarea = textareaRef.current;
                const currentPosition = textarea.selectionStart;
                const textBeforeCursor = textarea.value.substring(0, currentPosition);
                const lastNewLineIndex = textBeforeCursor.lastIndexOf('\n');
                const lastLine = textBeforeCursor.substring(lastNewLineIndex + 1);

                if (lastLine.startsWith('\t')) {
                    event.preventDefault();
                    const spaces = '\t'.repeat(lastLine.match(/^\t*/)[0].length); // рахуємо кількість табуляцій
                    const newText = textarea.value.substring(0, currentPosition) + '\n' + spaces + textarea.value.substring(currentPosition);
                    setText(newText);
                    textarea.setSelectionRange(currentPosition + spaces.length + 1, currentPosition + spaces.length + 1); // переміщення курсору
                }
            }
        };

        window.addEventListener('keydown', handleEnterPress);

        return () => {
            window.removeEventListener('keydown', handleEnterPress);
        };
    }, [text]);    

    // Додавання коментарів
    useEffect(() => {
        const handleComment = (event) => {
            if (event.ctrlKey && event.key === '/') {
                event.preventDefault();
                createOrRemoveComment();
            }
        };

        window.addEventListener('keydown', handleComment);

        return () => {
            window.removeEventListener('keydown', handleComment);
        };
    }, [createOrRemoveComment]);

    useEffect(() => {
        const textarea = textareaRef.current;
        const lineNumbersEle = document.getElementById('line-numbers');

        const displayLineNumbers = () => {
            const lines = text.split('\n');
            lineNumbersEle.innerHTML = Array.from({
                length: lines.length,
            }, (_, i) => `<div>${i + 1}</div>`).join('');
        };

        displayLineNumbers();

        const handleInput = (event) => {
            if (event.inputType === "insertText") {
                const { selectionStart, value } = event.target;
                const closingChars = {
                    '(': ')',
                    '{': '}',
                    '[': ']',
                    '"': '"',
                    "'": "'"
                };

                if (event.data in closingChars) {
                    event.preventDefault();
                    const closingChar = closingChars[event.data];
                    setText(value.substring(0, selectionStart) + closingChar + value.substring(selectionStart));
                    setTimeout(moveCursor, 0); // Виклик moveCursor з нульовою затримкою
                    textarea.selectionStart = selectionStart + 1;
                    textarea.selectionEnd = selectionStart + 1;
                } else if (event.data === closingChars[value[selectionStart]]) {
                    event.preventDefault();
                    textarea.selectionStart = selectionStart + 1;
                    textarea.selectionEnd = selectionStart + 1;
                    setTimeout(moveCursor, 0); // Виклик moveCursor з нульовою затримкою
                }
            }
        };

        textarea.addEventListener('input', handleInput);

        return () => {
            textarea.removeEventListener('input', handleInput);
        };
    }, [text]);

    const handleChange = (event) => {
        setText(event.target.value);
        const newHistory = [...history.slice(0, currentStep + 1), event.target.value];
        setHistory(newHistory);
        setCurrentStep(newHistory.length - 1);
    };

    return (
        <Container className='code-editor-container'>
            <div id="line-numbers" className="code-lines"></div>
            <textarea 
                ref={textareaRef}
                id="textarea" 
                value={text} 
                onChange={handleChange} 
                className='code-editor'>
            </textarea>
        </Container>
    );
}
