import React, { useState, useEffect } from 'react';
import { invoke } from '@tauri-apps/api/tauri';
import { save } from '@tauri-apps/api/dialog';
import TopBarMenu from './Components/Top_Bar_Menu_Component/Top_Bar_Menu';
import CodeEditor from './Components/Code_Editor_Component/Code_Editor';
import Help from './Components/Help_Component/Help';
import Terminal from './Components/Terminal_component/Terminal';
import './App.css';

function App() {
    const [terminalVisible, setTerminalVisible] = useState(false);
    const [helpVisible, setHelpVisible] = useState(false);
    const [filePath, setFilePath] = useState(null); // Стейт для зберігання шляху до файлу

    const toggleTerminal = () => {
        setTerminalVisible(!terminalVisible);
    };

    const toggleHelp = () => {
        setHelpVisible(!helpVisible);
    };

    const closeHelp = () => {
        setHelpVisible(false);
    };

    const saveFileContents = async (fileContent) => {
        try {
            let savePath = filePath;
            if (!savePath) {
                savePath = await save();
                if (!savePath) return;
                setFilePath(savePath);
            }
            await invoke("save_file", { path: savePath, contents: fileContent });
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div className="App">
            <TopBarMenu toggleTerminal={toggleTerminal} toggleHelp={toggleHelp} onSave={saveFileContents} />
            <CodeEditor onSave={saveFileContents} />
            {terminalVisible && <Terminal />}
            {helpVisible && <Help onClose={closeHelp} />}
        </div>
    );
}

export default App;
