import { useEffect, useState } from "react";
import { confirm } from "@tauri-apps/api/dialog";
import { useTranslation } from "react-i18next";
import { Container, Row } from "react-bootstrap";
import { exit } from "@tauri-apps/api/process";
import "./Top_Bar_Menu.css";

function DropdownMenu({ items }) {
  const [visible, setVisible] = useState(false);

  const handleClick = () => {
    setVisible(!visible);
  };

  const handleItemClick = (action) => {
    setVisible(false);
    action();
  };

  return (
    <div className="dropdown">
      <button className="dropdown-toggle" onClick={handleClick}>
        File
      </button>
      {visible && (
        <div className="dropdown-menu">
          {items.map((item, index) => (
            <button key={index} onClick={() => handleItemClick(item.action)}>
              {item.label}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}

export default function TopBarMenu({
  toggleTerminal,
  toggleHelp,
  onNew,
  onSave,
  onOpen,
}) {
  // Локалізація програми
  const { t, i18n } = useTranslation("global");

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleNew = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value != "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk == true) {
        handleSave();
        onNew();
      } else {
        onNew();
      }
    } else {
      onNew();
    }
  };

  // Отримання шляху файлу до файлу що відкрити його
  const handleOpen = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value != "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk == true) {
        handleSave();
        onOpen();
      } else {
        onOpen();
      }
    } else {
      onOpen();
    }
  };

  // Отримання тексту з textarea для збереження
  const handleSave = () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent);
  };

  // Отримання тексту з textarea для збереження як
  const handleSaveAs = async () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent, null);
  };

  // Прив'язка гарячих клавіш Ctrl+S та Ctrl+Shift+S
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.ctrlKey && event.key === "s") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent);
      }
      if (event.ctrlKey && event.shiftKey && event.key === "S") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent, null);
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [onSave]);

  // Закриття програми
  const exitApp = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value != "") {
      const saveAsk = await confirm(t("dialogue.exit_save"), "NUB IDE");
      if (saveAsk == true) {
        handleSave();
        await exit(0);
      } else {
        await exit(0);
      }
    } else {
      await exit(0);
    }
  };

  return (
    <Container className="top-bar-container">
      <Row className="top-bar-content">
        <Dropdown onToggle={toggleDropdown} show={dropdownOpen}>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            {t("header.file")}
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item onClick={handleNew}>{t("header.new")}</Dropdown.Item>
            <Dropdown.Item onClick={handleOpen}>
              {t("header.open")}
            </Dropdown.Item>
            <Dropdown.Item onClick={handleSave}>
              {t("header.save")}
            </Dropdown.Item>
            <Dropdown.Item onClick={handleSaveAs}>
              {t("header.save_as")}
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
        <button id="termButton" onClick={toggleTerminal}>
          {t("header.terminal")}
        </button>
        <button id="helpButton" onClick={toggleHelp}>
          {t("header.help")}
        </button>
        <button id="exitButton" onClick={exitApp}>
          {t("header.exit")}
        </button>
      </Row>
    </Container>
  );
}
