  //     <Top_Bar_Menu />
  //     <Code_Editor />
  //     {/* <File_Explorer /> */}
  //     <Terminal />
  //   </div>
  // );
  const [terminalVisible, setTerminalVisible] = useState(false);

  const toggleTerminal = () => {
    setTerminalVisible(!terminalVisible);
  };

  return (
    <div className="App">
      <Top_Bar_Menu toggleTerminal={toggleTerminal} />
      <Code_Editor />
      {/* <File_Explorer /> */}
      {terminalVisible && <Terminal />}
    </div>
  );
}

export default App;
