import { useEffect, useState } from "react";
import { confirm } from "@tauri-apps/api/dialog";
import { useTranslation } from "react-i18next";
import { Container, Row, Dropdown } from "react-bootstrap";
import { exit } from "@tauri-apps/api/process";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "./Top_Bar_Menu.css";

export default function TopBarMenu({
  toggleTerminal,
  toggleHelp,
  onNew,
  onSave,
  onOpen,
}) {
  // Локалізація програми
  const { t, i18n } = useTranslation("global");
  const [dropdownFileOpen, setFileDropdownOpen] = useState(false);
  const [dropdownEditOpen, setEditDropdownOpen] = useState(false);

  const handleNew = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk === true) {
        handleSave();
        onNew();
      } else {
        onNew();
      }
    } else {
      onNew();
    }
  };

  // Отримання шляху файлу до файлу що відкрити його
  const handleOpen = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.change_file_save"), "NUB IDE");
      if (saveAsk === true) {
        handleSave();
        onOpen();
      } else {
        onOpen();
      }
    } else {
      onOpen();
    }
  };

  // Отримання тексту з textarea для збереження
  const handleSave = () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent);
  };

  // Отримання тексту з textarea для збереження як
  const handleSaveAs = async () => {
    const textarea = document.getElementById("textarea");
    const fileContent = textarea.value;
    onSave(fileContent, null);
  };

  // Прив'язка гарячих клавіш Ctrl+S та Ctrl+Shift+S
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.ctrlKey && event.key === "s") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent);
      }
      if (event.ctrlKey && event.shiftKey && event.key === "S") {
        event.preventDefault();
        const textarea = document.getElementById("textarea");
        const fileContent = textarea.value;
        onSave(fileContent, null);
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [onSave]);

  // Закриття програми
  const exitApp = async () => {
    const textarea = document.getElementById("textarea");
    if (textarea.value !== "") {
      const saveAsk = await confirm(t("dialogue.exit_save"), "NUB IDE");
      if (saveAsk === true) {
        handleSave();
        await exit(0);
      } else {
        await exit(0);
      }
    } else {
      await exit(0);
    }
  };

  const handleFileDropdownToggle = () => {
    setFileDropdownOpen(!dropdownFileOpen);
  };

  const handleEditDropdownToggle = () => {
    setEditDropdownOpen(!dropdownEditOpen);
  };

  return (
    <Container className="top-bar-container">
      <Row className="top-bar-content">
        {/* Меню файл */}
        <Dropdown onToggle={handleFileDropdownToggle}>
          <Dropdown.Toggle variant="secondary" id="dropdown-basic">
            {t("header.file")}
          </Dropdown.Toggle>
          <Dropdown.Menu
            className={
              dropdownFileOpen
                ? "dropdown-content visible"
                : "dropdown-content hidden"
            }
          >
            <Dropdown.Item onClick={handleNew}>{t("header.new")}</Dropdown.Item>
            <Dropdown.Item onClick={handleOpen}>
              {t("header.open")}
            </Dropdown.Item>
            <Dropdown.Item onClick={handleSave}>
              {t("header.save")}
            </Dropdown.Item>
            <Dropdown.Item onClick={handleSaveAs}>
              {t("header.save_as")}
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
        {/* Меню редагування */}
        <Dropdown onToggle={handleEditDropdownToggle}>
          <Dropdown.Toggle variant="secondary" id="dropdown-basic">
            {t("header.edit")}
          </Dropdown.Toggle>
          <Dropdown.Menu
            className={
              dropdownEditOpen
                ? "dropdown-content visible"
                : "dropdown-content hidden"
            }
          >
            <Dropdown.Item onClick={handleSave}>
              {t("header.copy")}
            </Dropdown.Item>
            <Dropdown.Item onClick={handleSave}>
              {t("header.cut")}
            </Dropdown.Item>
            <Dropdown.Item onClick={handleSaveAs}>
              {t("header.paste")}
            </Dropdown.Item>
            <Dropdown.Divider style="width: 100%;" />
            <Dropdown.Item onClick={handleNew}>
              {t("header.undo")}
            </Dropdown.Item>
            <Dropdown.Item onClick={handleOpen}>
              {t("header.repo")}
            </Dropdown.Item>
            <Dropdown.Divider />
            <Dropdown.Item onClick={handleSave}>
              {t("header.selectAll")}
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
        {/*  */}
        <button id="termButton" onClick={toggleTerminal}>
          {t("header.terminal")}
        </button>
        <button id="helpButton" onClick={toggleHelp}>
          {t("header.help")}
        </button>
        <button id="exitButton" onClick={exitApp}>
          {t("header.exit")}
        </button>
      </Row>
    </Container>
  );
}
