import React, { useState, useRef, useEffect } from "react";
import { useTranslation } from "react-i18next";
import "./Terminal.css";
import { desktopDir } from "@tauri-apps/api/path";

export default function Terminal({ visible, filePath }) {
  const [inputValue, setInputValue] = useState("");
  const [outputValues, setOutputValues] = useState([]);
  const outputWrapperRef = useRef(null);
  const initialInputValue = "";
  const initialOutputValues = [];

  const [desktopPath, setDesktopPath] = useState([]);

  const getDir = async () => {
    setDesktopPath(await desktopDir());
  };

  useEffect(() => {
    getDir(); // Виклик функції
  }, []); // шлях до домашньої теки користувача
  const [pathForTerminal, setPathForTerminal] = useState(desktopPath); // Шлях який використовується тільки для терміналу

  // Локалізація програми
  const { t, i18n } = useTranslation("global");

  // Встановлення віртуального шляху для терміналу
  useEffect(() => {
    const lastSeparatorIndex = filePath.lastIndexOf("/");
    const trimmedPath = filePath.substring(0, lastSeparatorIndex);
    setPathForTerminal(trimmedPath);
  }, [filePath]);

  useEffect(() => {
    scrollToBottom();
  }, [outputValues]);

  const scrollToBottom = () => {
    if (outputWrapperRef.current) {
      outputWrapperRef.current.scrollTop =
        outputWrapperRef.current.scrollHeight;
    }
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      console.log("Enter pressed");
      const command = inputValue.trim();
      let result;
      // Команди терміналу
      // Змінити директорію (змінити змінну, яка використовується тільки для терміналу)
      if (command === "cd") {
      }
      // Показати вміст директорії (перевірити вміст директорії за віртуальним шляхом)
      else if (command === "ls") {
      }
      // Показати в якій директорії знаходиться користувач (знову ж таки віртуально)
      else if (command === "pwd") {
        result = pathForTerminal;
      }
      // Новий файл
      else if (command === "new" || command === "новий") {
        result = "Новий файл!";
      }
      // Відкриття файлу
      else if (command === "open" || command === "відкрити") {
        result = "Відкриття файлу!";
      }
      // Закрити термінал
      else if (command === "close" || command === "закрити") {
        result = "Термінал закрито";
      }
      // Допомога
      else if (command === "help" || command === "допомога") {
        result = "Довідка про функції NUB IDE";
      }
      // Змінити мову
      else if (command === "language" || command === "мова") {
        result = "Мову змінено";
      }
      // Встановити тему
      else if (command === "theme" || command === "тема") {
        result = "Тему змінено";
      }
      // Встановлення інтервалу автозбереження
      else if (command === "autosave" || command === "автозбереження") {
        result = "Новий інтервал встановлено";
      }
      // Закрити програму
      else if (command === "exit" || command === "вихід") {
        result = "Програму закрито";
      }
      // Очистити термінал
      else if (command === "clear" || command === "очистити") {
        result = "Термінал очищено!";
        setOutputValues(initialOutputValues);
        setInputValue(initialInputValue);
        return;
      }
      // Невідома команда
      else {
        result = t("terminal.error");
      }

      setOutputValues([...outputValues, { command: command, result: result }]);
      setInputValue("");
    }
  };

  return (
    <div className={`terminal-container ${visible ? "visible" : "hidden"}`}>
      <div className="terminal-header">
        <h5>Terminal</h5>
      </div>
      <div className="terminal-content">
        <div className="output-wrapper" ref={outputWrapperRef}>
          {outputValues.map((output, index) => (
            <div key={index} className="output">
              <div>{"> " + output.command}</div>
              <div>{output.result}</div>
            </div>
          ))}
        </div>
        <div className="input-wrapper">
          <span>₴ </span>
          <input
            type="text"
            placeholder="..."
            value={inputValue}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
          />
        </div>
      </div>
    </div>
  );
}
