import React, { useState, useEffect } from 'react';
import { Container, Row } from 'react-bootstrap';
import Dropdown from 'react-bootstrap/Dropdown';
import { useTranslation } from 'react-i18next';
import '../Standard-Component-style.css';
import './Help.css';

export default function Help({ onClose }) {
    const { t, i18n } = useTranslation("global");
    const [selectedLanguage, setSelectedLanguage] = useState(''); // початкове значення мови

    useEffect(() => {
        const storedLang = localStorage.getItem('language');
        if (storedLang) {
            handleLanguageChange(storedLang);
        } else {
            // Встановлення української мови за замовчуванням
            handleLanguageChange('ua');
        }
    }, []);

    const handleLanguageChange = (lang) => {
        i18n.changeLanguage(lang);
        setSelectedLanguage(lang);
        localStorage.setItem('language', lang);
    };

    return (
        <Container className='help-container'>
            <Row className='help-close-button'>
                <button onClick={onClose}><b>X</b></button>
            </Row>
            <Row className='help-content-container'>
                <div className='help-content'>
                    <h1>NUB IDE</h1>
                    <p>Це середовище розробки створене спеціально для мов програмування NukLang, Удав та Based</p>
                    <p>Ide має такі команди для терміналу:</p>
                    <p>- open - відкриває файл</p>
                    <p>- save - зберігає файл</p>
                    <p>- save as - зберегти файл як</p>
                    <p>- translate - викликає меню з вибором мови інтерфейсу</p>
                    <p>- close - закриває все IDE</p>
                    <Dropdown className='language-dropdown' onToggle={toggleDropdown} autoClose='inside'>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            {t("help.select_language")}
                        </Dropdown.Toggle>
                        <Dropdown.Menu className='language-dropdown-content'>
                            <Dropdown.Item href="#/action-1"><button onClick={() => handleLanguageChange("ua")}>Ukrainian</button></Dropdown.Item>
                            <Dropdown.Item href="#/action-2"><button onClick={() => handleLanguageChange("en")}>English</button></Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
            </Row>
        </Container>
    );
}
