import { useState } from 'react';
import { Container, Row } from 'react-bootstrap';
import { invoke } from '@tauri-apps/api/tauri';
import { writeTextFile } from '@tauri-apps/api/fs';
import { save } from '@tauri-apps/api/dialog';
import { exit } from '@tauri-apps/api/process';
import '../Standard-Component-style.css';
import './Top_Bar_Menu.css';

export default function TopBarMenu({ toggleTerminal, toggleHelp, onSave }) {
    const exitApp = async () => {
        await exit(0);
    };

    const [fileContent, setFileContent] = useState(""); // Стейт для збереження вмісту файлу

    const handleChange = (event) => {
        setFileContent(event.target.value); // Оновіть стейт змісту файла при зміні
    };

    const handleSave = async () => {
        try {
          const savePath = await save();
          if (!savePath) return;
          await invoke("save_file", { path: savePath, contents: fileContent });
          onSave(fileContent); // Викликайте функцію onSave з новим вмістом файла
        } catch (err) {
          console.error(err);
        }
      };

    // const [message, setMessage] = useState("");

    // const handleSave = async () => {
    //     try {
    //       const savePath = await save();
    //       if (!savePath) return;
    //       await invoke("save_file", { path: savePath, contents: message });
    //       console.log("File path:", savePath);
    //       console.log("File content:", message);

    //       /**
    //        * Інший варіант для зберігання файлів, але з жорстко встановленими ім'ям файлу та каталогом:
    //        * await writeTextFile("test.txt", message, {
    //        *    dir: BaseDirectory.Desktop,
    //        * });
    //        */
    //     } catch (err) {
    //       console.error(err);
    //     }
    //   };

    return (
        <Container className='top-bar-container'>
            <Row className='top-bar-content'>
                <button id='openButton'>Open</button>
                <button id='saveButton' onClick={handleSave}>Save</button>
                <button id='saveAsButton'>Save as</button>
                <button id='termButton' onClick={toggleTerminal}>Terminal</button>
                <button id='helpButton' onClick={toggleHelp}>Help</button>
                <button id='exitButton' onClick={exitApp}>Exit</button>
            </Row>
        </Container>
    );
}
